	public static function castArray($content) {
		try {
			$array = array();
			if(is_object($content)) {
				array_push($array, $content);
			}
			else if (is_array($content)) {
				$array = $content;
			}
			return $array;
		}
		catch (Exception $e) {
			 error_log($e->getMessage());
		}
	}

	public static function isEmpty($field) {
		return ($field === '' || $field === null || $field == false);
	}

	public static function isNull($field) {
		return ($field === 0 || $field === '' || $field === null || $field == false);
	}

	public static function equals($field, $compare) {
		return ($field === $compare);
	}

